mapper.update:

UPDATE report
SET
	report_status = '2',
	proceed_date = SYSDATE,
	stop_date = SYSDATE + #{plusStopDate}
WHERE report_no = #{reportNo}

mapper.select:
SELECT report_no, ref_post_no, ref_comment_no, user_id01, user_id02, report_what, report_status, report_category_no, report_date, proceed_date, report_content, stop_date FROM ( SELECT inner_table.*, ROWNUM AS row_seq FROM ( SELECT * FROM report WHERE report_status = #{searchCondition}
ORDER BY report_date DESC) inner_table
ORDER BY proceed_date DESC) inner_table
WHERE ROWNUM <= #{endRowNum} ) WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}
