mapper.select:
select
*
from (
	select
	inner_table.*, rownum as row_seq
	from (
		select
		*
		from matching
		where matchibg_status = '2'
	) inner_table
	where rownum <= #{endRowNum}
) where row_seq between #{startRowNum} and #{endRowNum}

mapper.update:
update item
second_user_id = #{secondUserId:VARCHAR}, use_result = #{useResult:VARCHAR}, use_date = sysdate
where item_no = #{itemNo}

mapper.select:
select
*
from (
	select
	inner_table.*, rownum as row_seq
	from (
		select
		*
		from item
	) inner_table
	where rownum <= #{endRowNum}
) where row_seq between #{startRowNum} and #{endRowNum}

mapper.sql:
select
*
from item
where first_user_id = #{userId}
and second_user_id is null
and item_category = #{itemCategory}
